import jsonimport pandas as pdimport numpy as np    portfolio_path = 'datasets/raw/portfolio.json'profile_path = 'datasets/raw/profile.json'transcript_path = 'datasets/raw/transcript.json'    output_path =  'datasets/cleaned/'        df = pd.read_json(portfolio_path, lines=True)df.to_csv (output_path + 'portfolio.csv', index = None)df = pd.read_json(profile_path, lines=True)df.to_csv (output_path + 'profile.csv', index = None)df = pd.read_json(transcript_path, lines=True)df['value'] = df['value'].astype(str)df['value']= df['value'].str.replace("{'offer id': ", '')df['value']= df['value'].str.replace("{'offer_id': ", '')df['value']= df['value'].str.replace("{'amount': ", '')df['value']= df['value'].str.replace("'", '')df['value']= df['value'].str.replace("}", '')df['reward'] = np.where(df.event == 'offer completed', df.value.apply(lambda x: x[-2:]) , None)# this part needs to be modified   df['value']= df['value'].str.replace(", reward: 2", '')df['value']= df['value'].str.replace(", reward: 3", '')df['value']= df['value'].str.replace(", reward: 5", '')df['value']= df['value'].str.replace(", reward: 10", '')    df.to_csv (output_path + 'transcript.csv', index = None)